"use strict";angular.module("personalApp",["ngRoute"]).config(["$routeProvider",function(a){a.when("/",{templateUrl:"views/main.html",controller:"MainCtrl"}).when("/about",{templateUrl:"views/about.html",controller:"AboutCtrl"}).when("/blog",{templateUrl:"views/blog.html",controller:"BlogCtrl"}).otherwise({redirectTo:"/"})}]),angular.module("personalApp").controller("MainCtrl",["$scope",function(a){a.init=function(){$("title").html("Volodya Skalskyi")},a.init()}]),angular.module("personalApp").controller("AboutCtrl",["$scope",function(a){a.init=function(){$("title").html("About")},a.init()}]),angular.module("personalApp").controller("BlogCtrl",["$scope","$http",function(a,b){a.posts=[],a.init=function(){$("title").html("Blog"),a.getPosts()},a.getPosts=function(){b.get("json/posts.json").success(function(b){a.posts=b,a.posts.posts.reverse()})},a.init()}]);